% PLOT2D - Contours 2-D surfaces%%  M. den Toom%function plot2D_zs(varx,vary,A,nsteps,cstep)%% - ERROR CHECK -if (ndims(A)~=2)    error('Sorry, you must use 2-D array for this plot function')elseif (size(A)~=[length(varx) length(vary)])    error('Length of coordinates vector does not match array size)')end%% - DETERMINE EXTREMA -maxval = max(max(A))minval = min(min(A))%% - DETERMINE CONTOURLINES -%cstep  = smartround( (maxval-minval)/nsteps,1);%cstep = 2;cons   = [fliplr(0:-cstep:minval-cstep) cstep:cstep:maxval+cstep];labs   = [fliplr(0:-2*cstep:minval-cstep) 2*cstep:2*cstep:maxval+cstep];%% - MAKE THE PLOT -contourf(varx,vary,A',cons,'Fill','on')colorbar