% READFORT3  - Reads the solution file of the experiment%%  H.A. Dijkstra%function [lab icp par xl xlp sig sol solup soleig] = ...    readfort3(n,m,filename)%fprintf(1,'Reading solution...\n')%% - OPEN FILE -fid      = fopen(filename,'r');if (fid==-1)    error('We regret to inform you that %s was not found.',filename)endnpar = 20; nun = 2; %% - READ HEADER -%version = fscanf(fid,'%*s %d',1);%fprintf(1,'-- this is solution output version %d.\n', version)%% - READ SOLUTION SPECS -lab     = fscanf(fid,'%d',1);icp     = fscanf(fid,'%d',1);nf      = fscanf(fid,'%d',1);ndim    = fscanf(fid,'%d',1);nskip   = fscanf(fid,'%d',1);%% - READ SPECIAL POINT CHARS -xl      = fscanf(fid,'%f',1);xlp     = fscanf(fid,'%f',1);det     = fscanf(fid,'%f',1);%% - READ EIGENVALUES -sig     = zeros(nf,2);for j = 1:nf    sig(j,:) = fscanf(fid,'%f',2);end%% - READ SOLUTION -sol     = zeros(n,m,nun);solup   = zeros(n,m,nun);soleig  = zeros(n,m,nf);for j = 1:m    for i = 1:n        for XX=1:nun            sol(i,j,XX)    = fscanf(fid,'%f',1);            solup(i,j,XX)  = fscanf(fid,'%f',1);            for j2=1:nf                soleig(i,j,XX,j2) = fscanf(fid,'%f',1);            end        end    endend%% - READ PARAMETER VALUES -par     = fscanf(fid,'%f',npar);%% - CLOSE FILE -fclose(fid);